const Hello = ({name, age}) => {
  const bornYear = () => {
    const year = new Date().getFullYear()
    return year - age 
  }
  return(
    <div>
      <p>Hello {name} you are {age} years old</p>
      <p>So you were probably born in {bornYear()}</p>
    </div>
  )
}

const App = () => {
  const name = 'Duncan'
  const age = 37
  return(
    <div>
      <h1>Greeting</h1>
      <Hello name={name} age={age}/>

    </div>
  )
}

export default App;
